import React from "react";
import ProductCard from "./ProductCard";
import ProductsData from "../../Assets/ProductsData";
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Productsection = () => {
   var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
  return (
    <>
      <section id="products">
        <h1>
          <span className="products-heading">Our products</span>
        </h1>
        <div className="container ">
          <div className="row">
            <div id="products-container ">
              <Slider {...settings}>
              {ProductsData.map((val, index) => {
                return (
                  <ProductCard
                    key={index}
                    image={val.image}
                    title={val.title}
                    para={val.para}
                    price = {val.price}
                  />
                );
              })}
              </Slider>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Productsection;
