import React from "react";
import ProductCard from "./ProductCard";
import ProductsData from "../Assets/ProductsData";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from 'react-slick';

const Productsection = () => {

      var settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 1,
              infinite: true,
              dots: false,
              arrows : false
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1,
              arrows : false
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              arrows : false
            }
          }
        ]
      };
  return (
    <>
      <section id="products">
        <h1>
          <span className="products-heading">Our products</span>
        </h1>
        <div className="container ">
          <div className="row">
            <div id="products-container ">
            <Slider >
              {ProductsData.map((val, index) => {
                return (
                  <ProductCard
                    key={index}
                    image={val.image}
                    title={val.title}
                    para={val.para}
                    price = {val.price}
                  />
                );
              })}
             </Slider>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Productsection;
